
import { useState, useEffect } from "react";
import { toast } from "@/hooks/use-toast";

export interface Department {
  id: string;
  name: string;
  status: "online" | "offline" | "warning";
  uptime: number;
  lastCheck: Date;
  responseTime: number;
  devicesConnected: number;
  ipRange: string;
}

export interface Alert {
  id: string;
  departmentId: string;
  departmentName: string;
  type: "network_down" | "high_latency" | "device_offline";
  severity: "critical" | "warning" | "info";
  message: string;
  timestamp: Date;
  acknowledged: boolean;
}

export const useNetworkMonitoring = () => {
  const [departments, setDepartments] = useState<Department[]>([
    {
      id: "1",
      name: "IT Department",
      status: "online",
      uptime: 99.8,
      lastCheck: new Date(),
      responseTime: 12,
      devicesConnected: 45,
      ipRange: "192.168.1.0/24"
    },
    {
      id: "2",
      name: "Sales Department",
      status: "online",
      uptime: 98.5,
      lastCheck: new Date(),
      responseTime: 18,
      devicesConnected: 32,
      ipRange: "192.168.2.0/24"
    },
    {
      id: "3",
      name: "Engineering",
      status: "warning",
      uptime: 97.2,
      lastCheck: new Date(),
      responseTime: 45,
      devicesConnected: 67,
      ipRange: "192.168.3.0/24"
    },
    {
      id: "4",
      name: "HR Department",
      status: "online",
      uptime: 99.1,
      lastCheck: new Date(),
      responseTime: 15,
      devicesConnected: 18,
      ipRange: "192.168.4.0/24"
    },
    {
      id: "5",
      name: "Finance",
      status: "offline",
      uptime: 85.3,
      lastCheck: new Date(),
      responseTime: 0,
      devicesConnected: 0,
      ipRange: "192.168.5.0/24"
    }
  ]);

  const [alerts, setAlerts] = useState<Alert[]>([
    {
      id: "1",
      departmentId: "5",
      departmentName: "Finance",
      type: "network_down",
      severity: "critical",
      message: "Finance department network is completely down. All devices unreachable.",
      timestamp: new Date(Date.now() - 300000), // 5 minutes ago
      acknowledged: false
    },
    {
      id: "2",
      departmentId: "3",
      departmentName: "Engineering",
      type: "high_latency",
      severity: "warning",
      message: "High network latency detected (45ms). Performance may be degraded.",
      timestamp: new Date(Date.now() - 120000), // 2 minutes ago
      acknowledged: false
    }
  ]);

  // Simulate network monitoring
  useEffect(() => {
    const interval = setInterval(() => {
      setDepartments(prev => prev.map(dept => {
        // Simulate network status changes
        const random = Math.random();
        let newStatus = dept.status;
        
        if (dept.id === "5") {
          // Finance stays offline for demo
          newStatus = "offline";
        } else if (random < 0.05) {
          newStatus = random < 0.02 ? "offline" : "warning";
        } else {
          newStatus = "online";
        }

        // Update response time based on status
        let responseTime = dept.responseTime;
        if (newStatus === "offline") {
          responseTime = 0;
        } else if (newStatus === "warning") {
          responseTime = 35 + Math.random() * 20;
        } else {
          responseTime = 10 + Math.random() * 15;
        }

        return {
          ...dept,
          status: newStatus,
          lastCheck: new Date(),
          responseTime: Math.round(responseTime),
          devicesConnected: newStatus === "offline" ? 0 : dept.devicesConnected
        };
      }));

      // Generate new alerts for offline departments
      setDepartments(currentDepts => {
        currentDepts.forEach(dept => {
          if (dept.status === "offline" && dept.id !== "5") {
            const existingAlert = alerts.find(a => a.departmentId === dept.id && !a.acknowledged);
            if (!existingAlert) {
              const newAlert: Alert = {
                id: Date.now().toString(),
                departmentId: dept.id,
                departmentName: dept.name,
                type: "network_down",
                severity: "critical",
                message: `${dept.name} network is down. Immediate attention required.`,
                timestamp: new Date(),
                acknowledged: false
              };
              setAlerts(prev => [newAlert, ...prev]);
              
              // Show toast notification
              toast({
                title: "ðŸš¨ Network Alert",
                description: newAlert.message,
                variant: "destructive",
              });

              // Play alert sound
              try {
                const audio = new Audio("data:audio/wav;base64,");
                audio.volume = 0.3;
                audio.play();
              } catch (e) {
                console.log("Could not play alert sound");
              }
            }
          }
        });
        return currentDepts;
      });
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const acknowledgeAlert = (alertId: string) => {
    setAlerts(prev => prev.map(alert => 
      alert.id === alertId 
        ? { ...alert, acknowledged: true } 
        : alert
    ));
    
    toast({
      title: "Alert Acknowledged",
      description: "The alert has been marked as acknowledged.",
    });
  };

  const getActiveAlertsCount = () => {
    return alerts.filter(a => !a.acknowledged).length;
  };

  return {
    departments,
    alerts,
    acknowledgeAlert,
    getActiveAlertsCount
  };
};
